mapper.update:
UPDATE team 
set
	team_name = #{teamName}
	,team_address = #{teamAddress}
	,team_level = #{teamLevel}
	,team_info = #{teamInfo}
WHERE team_idx = #{teamIdx}

mapper.select:
select logo
from team
where team_idx = #{teamIdx}

mapper.update:
UPDATE team_join
	SET join_team_content = #{param2}
	,join_team_level = #{param3}
	,join_team_state = #{param4}
	WHERE join_team_idx = #{param1}
